mapper.update:
update account
set balance = balance + #{money}
where name = #{account}
  and user_id = #{userId}

update account
set balance = balance - #{money}
where name = #{account}
  and user_id = #{userId}

mapper.select:
select sum(balance)
from account
where user_id = #{userId}

select name, balance
from account
where user_id = #{userId}
